def get_date(eacItem):
    return eacItem["date"]

def home(request):
    sorted_list=sorted(post,key=get_date)
    latest_posts=sorted_list[-3:]
    return render(request,"blog/home.html",{"posts":latest_posts})

def postDetails(request,slug):
    obj=next(obj for obj in post if obj["slug"]==slug)
    return render(request,"blog/postDetails.html",{"obj":obj})

def posts(request):
    return render(request,"blog/posts.html",{"posts":post})